mapper.select:
SELECT id, user_id, book_id, reserve_time, borrow_time, status, create_time, update_time,user_name,book_title
FROM reserve_info
WHERE id = #{id}

mapper.update:
UPDATE reserve_info
SET user_id = #{userId}, book_id = #{bookId}, reserve_time = #{reserveTime}, borrow_time = #{borrowTime},
status = #{status}, update_time = #{updateTime}
WHERE id = #{id}

update reserve_info
set status = 1
where id = #{id}

mapper.select:
SELECT id, user_id, book_id, reserve_time, borrow_time, status, create_time, update_time,user_name,book_title
FROM reserve_info
WHERE user_id = #{userId}

SELECT id, user_id, book_id, reserve_time, borrow_time, status, create_time, update_time,user_name,book_title
FROM reserve_info
WHERE book_id = #{bookId}

SELECT id, user_id, book_id, reserve_time, borrow_time, status, create_time, update_time,user_name,book_title
FROM reserve_info
WHERE status = #{status}
